import ConfigurationContext from '../../context/ConfigurationContext'
import './index.css'

const Body = () => {
  return (
    <ConfigurationContext.Consumer>
      {value => {
        const {showContent, showLeftNavbar, showRightNavbar} = value
        return (
          <div className="body-container">
            {(showLeftNavbar===true && showContent===true && showRightNavbar===true) ? (
              <div className="left-menu-container">
                <h1 className="heading-body">Left Navebar Menu</h1>
                <ul>
                  <li>Item 1</li>
                  <li>Item 2</li>
                  <li>Item 3</li>
                  <li>Item 4</li>
                </ul>
              </div>
            ) : (
              <div className="left-menu-container-false"></div>
            )}
            {showContent ? (
              <div className="content-menu-container">
                <h1 className="heading-body">Content</h1>
                <p>
                  Lorem ipsum dolor sit amet consectetur adiplscing elit sed do
                </p>
              </div>
            ) : (
              <div className="content-ment-container-false"></div>
            )}
            {showRightNavbar ? (
              <div className="right-menu-container">
                <h1 className="heading-body">Right Navebar</h1>
                <div className="right-box">Ad1</div>
                <div className="right-box">Ad2</div>
              </div>
            ) : (
              <div className="right-menu-container-false"></div>
            )}
          </div>
        )
      }}
    </ConfigurationContext.Consumer>
  )
}

export default Body
