import ConfigurationContext from '../../context/ConfigurationContext'
import './index.css'

const Body = () => {

  const getTheStatus = (showContent, showLeftNavbar, showRightNavbar) =>{
    let status
    if(showContent===true && showLeftNavbar===true && showRightNavbar=== true){
      return  status = "caseA"
    }else if(showContent===false && showLeftNavbar===true && showRightNavbar=== true){
      return status = "caseB"
    }else if(showContent===true && showLeftNavbar===false && showRightNavbar=== false){
      return status = "caseC"
    }else if(showContent===true && showLeftNavbar===true && showRightNavbar=== false){
      return status = "caseD"
    }else if(showContent===false && showLeftNavbar===false && showRightNavbar=== true){
      return status = "caseE"
    }else if(showContent===true && showLeftNavbar===false && showRightNavbar=== true){
      return status ="caseF"
    }else if(showContent===false && showLeftNavbar===false && showRightNavbar=== false){
      return status="caseG"
    }
    console.log(status)
    return status
  }

  return (
  <ConfigurationContext.Consumer>
    {value => {
      const {showContent, showLeftNavbar, showRightNavbar} = value
      return (
        <div className="body-container">
        {showLeftNavbar ?( <div className="left-menu-container">
                <h1 className="heading-body">Left Navebar Menu</h1>
                <ul>
                  <li>Item 1</li>
                  <li>Item 2</li>
                  <li>Item 3</li>
                  <li>Item 4</li>
                </ul>
              </div>):
              <div className="left-menu-container-false"></div>
              }
          {showContent ? (<div className="content-menu-container">
                <h1 className="heading-body">Content</h1>
                <p>
                  Lorem ipsum dolor sit amet consectetur adiplscing elit sed do
                </p>
              </div>): (<div className="content-ment-container-false"></div>)
              }
              {showRightNavbar  ( <div className="right-menu-container">
                <h1 className="heading-body">Right Navebar</h1>
                <div className="right-box">Ad1</div>
                <div className="right-box">Ad2</div>
              </div>)}
        </div>
      )
    }}
  </ConfigurationContext.Consumer>
  )
}

export default Body
