          showRightNavbar === true
        ){
          status = true
        }else {
          status = false
        }
        return status
      }


  return (
  <ConfigurationContext.Consumer>
    {value => {
      const {showContent, showLeftNavbar, showRightNavbar} = value

      return (
        <div className="body-container">
          {caseA && (
            <div className="case-A">
